export const getFormattedDateString = (date, time = '') => {
  if (!date) return 'Date not available';
  console.log(date + ' ' + time);
  return moment(date + ' ' + time).format('D MMM h:mm a');
};

export const getFormattedDateString_old = date => {
  if (!date) return 'Date not available';
  const newDate = new Date(date);
  const options = {
    day: '2-digit',
    month: 'short'
  };
  if (newDate.getHours() || newDate.getMinutes()) {
    options.hour = '2-digit';
    options.minute = '2-digit';
    options.hourCycle = 'h12';
  }
  return newDate.toLocaleString('en-NZ', options);
};
